import React, { useState } from 'react';

function Search() {

    const [animename, setanimename] = useState('');
    const [searched, setsearched] = useState(false);

    const handleSearch = async() =>{
        alert('anime search');
        
        /*let storedlist=sessionStorage.getItem('animelist');
        if(storedlist){
            let animelist=JSON.parse(storedlist);
            animelist=[...animelist,animename];
            sessionStorage.setItem('animelist',JSON.stringify(animelist));
        }
        return setanimename('');*/
      const response = await fetch(`https://animeschedule.net/api/v3/anime?q=${animename}`);
      const data = await response.json();
        console.log("DATA FROM SEARCH-", data);
        return data;
    }


    return (
      <>
      {searched ? (
        <div className='body_home'>
          <div className='container'>
              <label>
                Anime name:
                <input
                  type="text"
                  placeholder='Case Sensitive'
                  value={animename}
                  onChange={(e) => setanimename(e.target.value)}
                />
              </label>
            <button onClick={()=>{setsearched(true)}}>Search</button>
          </div>
        </div>
        ):
        (
          <>
            {handleSearch()}
          </>
        )
        }</>
        );
}

export default Search;
