import React, { useState } from "react";
import ShowData from "./ShowData";
import axios from "axios";

function Search() {
  const [animename, setanimename] = useState("");
  const [searched, setsearched] = useState(false);
  const [data, setdata] = useState([]);

  const search = async () => {
    if (animename !== "") {
      
      try{
       
       /*
        const urrl='/api';
        console.log("URL ",urrl);
        const options = { 
          method: 'GET', 
          url: urrl, 
          params: {q:animename}, 
          headers: {"accepts":"application/json"} 
      }; 
      
      return axios.request(options)
          .then(res => { setdata(res.data); })
          .catch((err) => { return err; }) ;

      if(data.length>0){
        setsearched(true);
      }

      */
        
      const response = await fetch("https://animeschedule.net/api/v3/anime?q=${animename}",{
        headers: {
          "Content-Type":"application/json"
        },
        method:"post",
      });
      console.log("Response FROM SEARCH fetch-", response);
      const data = await response.data();
      console.log("DATA FROM SEARCH-", data);
      alert("anime search done");
      setdata(data);
      setsearched(true);
      return data;
      
    }
      catch (error) {
        console.error('Error during search:', error);
      }
    }
    };
  
  const showList = () => {
    
    alert("anime search "+animename);
    return(
      data
    );
    /*let storedlist=sessionStorage.getItem('animelist');
        if(storedlist){
            let animelist=JSON.parse(storedlist);
            animelist=[...animelist,animename];
            sessionStorage.setItem('animelist',JSON.stringify(animelist));
        }
        return setanimename('');*/
        //console.log("SEARCH init-",search());
  };

  return (
    <>
      {searched ? (
        <ShowData data={data} />
      ) : (
        <div className="body_home">
          <div className="container">
            <label>
              Anime name:
              <input
                type="text"
                placeholder="Case Sensitive"
                value={animename}
                onChange={(e) => {setanimename(e.target.value)}}
              />
            </label>
            <button
              onClick={(e) => {
                search(e);
              }}>
              Search
            </button>
          </div>
        </div>
      )}
    </>
  );
}

export default Search;
