import React, { useState } from "react";

function Search() {
  const [animename, setanimename] = useState("");
  const [searched, setsearched] = useState(false);

  const handleSearch = () => {
    alert("anime search");

    /*let storedlist=sessionStorage.getItem('animelist');
        if(storedlist){
            let animelist=JSON.parse(storedlist);
            animelist=[...animelist,animename];
            sessionStorage.setItem('animelist',JSON.stringify(animelist));
        }
        return setanimename('');*/
    async () => {
    if (false &&  animename !== "") {
      try{
      let url = `https://animeschedule.net/api/v3/anime?q=${animename}`;
      console.log("URL FOR FETCH", url);
      const response = await fetch(url);
      console.log("Response FROM SEARCH fetch-", response);
      const data = await response.data();
      console.log("DATA FROM SEARCH-", data);
      alert("anime search done");
      return data;
      }
      catch (error) {
        console.error('Error during search:', error);
      }
    }
  }
  };

  return (
    <>
      {searched ? (
        <div>{handleSearch()}</div>
      ) : (
        <div className="body_home">
          <div className="container">
            <label>
              Anime name:
              <input
                type="text"
                placeholder="Case Sensitive"
                value={animename}
                onChange={(e) => setanimename(e.target.value)}
              />
            </label>
            <button
              onClick={() => {
                setsearched(true);
              }}
            >
              Search
            </button>
          </div>
        </div>
      )}
    </>
  );
}

export default Search;
