import React, { useState, useEffect } from 'react';
import axios from "axios";
import './Home.css';
import ShowData from './ShowData';

function Home() {

    const [data, setdata] = useState([]);

  useEffect(() => {
    getData();
  }, []); // Adding an empty dependency array to make sure the effect runs only once

  const getData = async () => {
    try {
      const url = "http://localhost:5000/api/route?route=" + "tate-no-yuusha-no-nariagari-2";
      console.log("INSIDE HOME URL-", url);

      const res = await axios.get(url);
      console.log("RESPONSE FROM API CALL-", res);
      console.info("data for home- ", res.data);
      let tempdata=data;
      console.info("TEMP data for home- ", tempdata);
      setdata([tempdata,res.data]);
      console.log("data set- ", data);
    } catch (error) {
      console.log("RESPONSE FROM API CALL ERROR-", error);
    }
  };

  return (
    <div>
        {(data.length > 0) && (
        <ShowData data={data} />
        )}
            
    </div>
  );
}

export default Home;
