import React, { useState } from 'react';
import './Home.css';

function Home({setpage,setUser}) {

    const [username, setUsername] = useState('');

    const handleLogin = () => {
        // Simulate a login check (you can add actual authentication logic here)
        if (username.trim() !== '') {
          //alert('Login successful!');
          const namesRaw=localStorage.getItem('username');
          let names=[];
          if(namesRaw){
            names=JSON.parse(namesRaw);
            if(names.includes(username)){
                //get user list
            }
            else{
                names=[...names, username];
                localStorage.setItem('username',names);
            }
          }
          else{
            localStorage.setItem('username',username);
        }
          setUser(username);
          setpage('Search');
        }
        else {
          alert('Please enter a username.');
        }
      };

    return (
    <div className='body_home'>
    <div className='container'>
    
          <label>
            Username:
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </label>
          <button onClick={handleLogin}>Login</button>
    </div>
    </div>
    );
}

export default Home;
